import React, { useState } from "react";
import AccordionItem from "./AccordionItem";

const Accordion = ({ faqs }) => {
  const [curOpen, setCurOpen] = useState(null);

  return (
    <>
      {faqs.map((element, index) => (
        <AccordionItem
          curOpen={curOpen}
          onOpen={setCurOpen}
          key={index}
          title={element.title}
          text={element.text}
          index={index}
        />
      ))}
    </>
  );
};

export default Accordion;
